import type * as Monaco from 'monaco-editor'

/**
 * Standard Monaco editor instance.
 */
export type EditorInstance = Monaco.editor.IStandaloneCodeEditor

/**
 * Standard Monaco editor model.
 */
export type EditorModel = Monaco.editor.ITextModel

/**
 * Standard Monaco editor options.
 */
export type EditorOptions = Monaco.editor.IStandaloneEditorConstructionOptions
